import axios from "axios";
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

function Search() {
  const [query, setQuery] = useState("");
  const [results, setResults] = useState([]);
  const navigate = useNavigate();

  const searchMovies = async (e) => {
    e.preventDefault();
    const response = await axios.get(
      `https://api.themoviedb.org/3/search/movie?api_key=9d2bff12ed955c7f1f74b83187f188ae&query=${encodeURIComponent(
        query
      )}`
    );
    setResults(response.data.results);
  };

  return (
    <div style={{ textAlign: "center", marginTop: "20px" }}>
      <form onSubmit={searchMovies} style={{ marginBottom: "20px" }}>
        <input
          type="text"
          value={query}
          placeholder="영화 제목을 입력해 주세요."
          onChange={(e) => setQuery(e.target.value)}
          style={{ width: "300px", padding: "10px", fontSize: "16px" }} // 검색창 크기 조절
        />
        <button
          type="submit"
          style={{ padding: "10px 20px", marginLeft: "10px", fontSize: "16px" }}
        >
          영화 찾기
        </button>
      </form>
      <ul style={{ listStyle: "none", padding: 0 }}>
        {results.map((movie) => (
          <li
            key={movie.id}
            onClick={() => navigate(`/detail/${movie.id}`)}
            style={{
              cursor: "pointer",
              padding: "10px 0",
              borderBottom: "1px solid #ccc",
            }}
          >
            {movie.title}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Search;
